"""File: class_reviews.pyName:-------------------------------At the beginning of this program, the user is asked to inputthe class name (either SC001 or SC101).Attention: your input should be case-insensitive.If the user input "-1" for class name, your program would showthe maximum, minimum, and average among all the inputs."""EXIT = -1def main():    print('Here is the scoreboard for class stanCode!')    klass = give_klass()    if klass == str(EXIT):        print('No class scores were entered.')    else:        high_001 = low_001 = sum_001 = times_001 = high_101 = low_101 = sum_101 = times_101 = 0  # 計分板上的數據的預設值        score = give_score()  # 第 1 次成績輸入        if klass == 'SC001':            high_001 = low_001 = sum_001 = score            times_001 = 1        elif klass == 'SC101':            high_101 = low_101 = sum_101 = score            times_101 = 1        while True:            klass = give_klass()            if klass == str(EXIT):                break            elif klass == 'SC001':  # 針對不同班級，更新計分板上的數據                score = give_score()                high_001, low_001, sum_001, times_001 = calculate(score, high_001, low_001, sum_001, times_001)            elif klass == 'SC101':                score = give_score()                high_101, low_101, sum_101, times_101 = calculate(score, high_101, low_101, sum_101, times_101)        print_scoreboard('SC001', high_001, low_001, sum_001, times_001)  # 印出計分板上的最終結果        print_scoreboard('SC101', high_101, low_101, sum_101, times_101)def give_klass():    klass = str(input('Which class? ')).upper()    while True:        if klass == 'SC001' or klass == 'SC101' or klass == str(EXIT):            break        else:            print('The class name only allowed: SC001, SC101 (or give -1 to exit)')            klass = str(input('Which class? ')).upper()    return klassdef give_score():    score = str(input('Score: '))    while not score.isdigit():        print('Please give a positive integer as score.')        score = str(input('Score: '))    score = int(score)    return scoredef calculate(score, high, low, sum, times):    if times == 0:  # 代表第 1 次為該班級輸入分數        high = low = score    elif score > high:  # 高於最高分        high = score    elif score < low:  # 低於最低分        low = score    sum += score    times += 1    return high, low, sum, timesdef print_scoreboard(klass, high, low, sum, times):    print('=============' + str(klass) + '=============')    if times == 0:        print('No score for ' + str(klass))    else:        avg = sum / times  # 計算平均值        print('MAX (' + str(klass[2:]) + '): ' + str(high))        print('MIN (' + str(klass[2:]) + '): ' + str(low))        print('AVG (' + str(klass[2:]) + '): ' + str(avg))# ---- DO NOT EDIT CODE BELOW THIS LINE ---- #if __name__ == '__main__':    main()